import React, { Component } from 'react';
import './portfolio.css';
import Port1 from '../../assets/port1.png';
import Port2 from '../../assets/port2.png';
import Port3 from '../../assets/port3.png';
import Port4 from '../../assets/port4.png';
import Port5 from '../../assets/port5.png';
import Port6 from '../../assets/port6.png';
import Port7 from '../../assets/port7.png';

import graph1 from '../../assets/1.jpg';
import graph2 from '../../assets/2.jpg';
import graph3 from '../../assets/3.jpg';
import graph4 from '../../assets/4.jpg';
import graph5 from '../../assets/5.mp4';
import graph6 from '../../assets/6.jpg';
import graph7 from '../../assets/7.jpg';
import { useState } from 'react';



// Do NOT USE THE IMAGES IN PRODUCTION

const webData = [
 {
  id: 1,
  webImage: Port1,
  webTitle: 'My Portfolio',
  webDesc: 'responsive website using (html,css,bootstrap,javascript)',
  webGithub: 'https://github.com',
  webDemo: 'https://github.com'
},
 {
  id: 2,
  webImage: Port2,
  webTitle: 'It Network',
  webDesc: 'responsive website using (html,css,bootstrap,javascript)',
  webGithub: 'https://github.com',
  webDemo: 'https://github.com'
},
 {
  id: 3,
  webImage: Port3,
  webTitle: 'Fresh Shop',
  webDesc: 'responsive website using (html,css,bootstrap,javascript)',
  webGithub: 'https://github.com',
  webDemo: 'https://github.com'
},
 {
  id: 4,
  webImage: Port4,
  webTitle: 'Faster Motors',
  webDesc: 'responsive website using (html,css,bootstrap,javascript)',
  webGithub: 'https://github.com',
  webDemo: 'https://github.com'
},
 {
  id: 5,
  webImage: Port5,
  webTitle: 'Alkasr Studio',
  webDesc: 'responsive website using (html,css,bootstrap,javascript)',
  webGithub: 'https://github.com',
  webDemo: 'https://github.com'
},
 {
  id: 6,
  webImage: Port6,
  webTitle: 'Prush for decore paints',
  webDesc:'responsive website using (html,css,bootstrap,javascript)',
  webGithub: 'https://github.com',
  webDemo: 'https://github.com'
},
 {
  id: 7,
  webImage: Port7,
  webTitle: 'Dashboard',
  webDesc: 'responsive website using (html,css,bootstrap)',
  webGithub: 'https://github.com',
  webDemo: 'https://github.com'
},
]

const graphicData = [
  {
    id: 1,
    gTitle: graph1,
    gTitle: 'It Network',
    gDesc: 'responsive website using (html,css,bootstrap,javascript)',
    gGithub: 'https://github.com',
    gDemo: 'https://github.com'
  },
  {
   id: 2,
   gTitle: graph2,
   gTitle: 'It Network',
   gDesc: 'responsive website using (html,css,bootstrap,javascript)',
   gGithub: 'https://github.com',
   gDemo: 'https://github.com'
 },
  {
   id: 3,
   gTitle: graph3,
   gTitle: 'Fresh Shop',
   gDesc: 'responsive website using (html,css,bootstrap,javascript)',
   gGithub: 'https://github.com',
   gDemo: 'https://github.com'
 },
  {
   id: 4,
   gTitle: graph4,
   gTitle: 'Faster Motors',
   gDesc: 'responsive website using (html,css,bootstrap,javascript)',
   gGithub: 'https://github.com',
   gDemo: 'https://github.com'
 },
  {
   id: 5,
   gImage: graph5,
   gTitle: 'Alkasr Studio',
   gDesc: 'responsive website using (html,css,bootstrap,javascript)',
   gGithub: 'https://github.com',
   gDemo: 'https://github.com'
 },
  {
   id: 6,
   gImage: graph6,
   gTitle: 'Prush for decore paints',
   gDesc:'responsive website using (html,css,bootstrap,javascript)',
   gGithub: 'https://github.com',
   gDemo: 'https://github.com'
 },
  {
   id: 7,
   gImage: graph7,
   gTitle: 'Dashboard',
   gDesc: 'responsive website using (html,css,bootstrap)',
   gGithub: 'https://github.com',
   gDemo: 'https://github.com'
 },
 ]
function Portfolio() {
     
      const [toggleState, setToggleState] = useState(1);

      const toogleTab = (index) => {
        setToggleState(index);
      }
        return (
             <section id='portfolio' >
               <h5>My React Work</h5>
               <h2>Portfolio</h2>
                <div className='block-tabs'>
                  <div className={toggleState === 1 ? "tabs active-tabs" : "tabs"}
                   onClick={() =>toogleTab(1)}>Web Development</div>

                  <div className={toggleState === 2 ? "tabs active-tabs" : "tabs"} 
                  onClick={() =>toogleTab(2)}>Graphic Designing</div>
                </div>
                <div className="container portfolio__container">
                  <div className={toggleState === 1 ? "content active-content" : "content"}>
                    {
                      webData.map(({id, webImage, webTitle, webDesc, webGithub, webDemo}) => {
                        return (
                          <article className='portfolio__item'>
                          <div className="portfolio__item-image">
                            <img src={webImage} alt={webTitle}/>
                          </div>
                          <div className="portfolio__item-details ">
                          <h3>{webTitle}</h3>
                          <p>{webDesc}</p>
                          </div>
                          <div className="portfolio__item-cta ">
                          <a href={webGithub} className="btn" target='_blank'>Github</a>
                          <a href={webDemo} className="btn btn-primary" target='_blank'>Live Demo</a>
                          </div>
                        </article>
                        )
                      })
                    }
                  </div>
                  <div className={toggleState === 2 ? "content active-content" : "content"}>
                  {
                    graphicData.map(({id, gImage, gTitle, gDesc, gGithub, gDemo}) => {
                      return (
                        <article className='portfolio__item'>
                        <div className="portfolio__item-image">
                          <img src={gImage} alt={gTitle}/>
                        </div>
                        <div className="portfolio__item-details ">
                        <h3>{gTitle}</h3>
                        <p>{gDesc}</p>
                        </div>
                        <div className="portfolio__item-cta ">
                        <a href={gGithub} className="btn" target='_blank'>Github</a>
                        <a href={gDemo} className="btn btn-primary" target='_blank'>Live Demo</a>
                        </div>
                      </article>
                      )
                    })
                  }
                  </div>
                </div>
            </section> 
        )
    };
export default Portfolio;
