import React, { Component } from 'react';
import './contact.css';
import {MdOutlineEmail} from 'react-icons/md';
import {RiMessengerLine} from 'react-icons/ri';
import {BsWhatsapp} from 'react-icons/bs';
import { useRef } from 'react';
import emailjs from 'emailjs-com';



const Contact = ()=> {

	const form =useRef()
	const sendEmail = (e) => {
        e.preventDefault();

       emailjs.sendForm('service_1heos0l', 'template_gkno955', form.current, 'cnPFS7uNaxn0ShtpF')
		e.target.reset()
      };

        return (
            <section id='contact' >
               <h5>Get In Touch</h5>
               <h2>Contact Me</h2>

	       <div className="container contact__container">
		 <div className="contact__options">
	 	   <article className="contact__option">
			<MdOutlineEmail className="contact__option-icon" />
			<h4>Email</h4>
			<h3>saidsadaoy@gmail.com</h3>
			<a href="mailto:saidsadaoy@gmail.com" target="_blank">Send a message</a>
	           </article>
	 	   <article className="contact__option">
			<RiMessengerLine className="contact__option-icon" />
			<h4>Messenger</h4>
			<h5>elsaaeid ellithy</h5>
			<a href="https://m.me/saaed.sadaoy" target="_blank">Send a message</a>
	           </article>
	 	   <article className="contact__option">
			<BsWhatsapp className="contact__option-icon" />
			<h4>WhatsApp</h4>
			<h5>+20 01028496209</h5>
			<a href="https://api.whatsapp.com/send?phone=+2001028496209" target="_blank">Send a message</a>
	           </article>
	         </div>
		{/* END OF CONTACT OPTOINS */}
		<form ref={form} onSubmit={sendEmail}>
		   <input type="text" name="name" placeholder="Your Full Name" required />
		   <input type="email" name="email" placeholder="Your Email" required />
		   <textarea name="message" rows="7" placeholder="Your Message" required ></textarea>	
		   <button className="btn btn-primary" type="submit">Send Message</button>
		</form>
	      </div>
            </section> 
        )
}
export default Contact;
